import React, { useState } from 'react';
import { render } from 'react-dom';
import { DatePicker, message, Alert } from 'antd';
import 'antd/dist/antd.css';
import './index.css';
import { BrowserRouter, Route, Switch } from "react-router-dom";


class OboutMe extends React.Component {
  render() {

    return (
      <BrowserRouter>
        <Name name={this.props.name} />
        <Age age={23} />
        <Gender gender={this.props.gender} />
        <Family dog='Chappy' />
        <Family dog='Juchka' />
        <Family />
        <DatePicker />
      </BrowserRouter>
    );
  }
}

class Family extends React.Component {
  render() {
    const myFamily = {
      dad: 'Jack',
      mom: 'Lusy',
    };
    if (this.props.dog) {
      return (
        <p>My family is my dad {myFamily.dad}, my mom {myFamily.mom} and my dog {this.props.dog}  and we are live together.</p>
      );
    }
    return (
      <p>My family is my dad {myFamily.dad} and my mom {myFamily.mom} and we are live together.</p>
    );

  }
}

function Age(props) {
  const [date, setDate] = useState(null);
  const handleChange = value => {
    message.info(`Selected Date: ${value ? value.format('YYYY-MM-DD') : 'None'}`);
    setDate(value);
  };
  return (
    <div style={{ width: 400, margin: '100px auto' }}>
      <DatePicker onChange={handleChange} />
      <div style={{ marginTop: 16 }}>
        <Alert message="Selected Date" description={date ? date.format('YYYY-MM-DD') : 'None'} />
      </div>
    </div>
  );
}
function Name(props) {
  return (
    <p>{props.name}</p>
  );
}
function Gender(props) {
  return (
    <p>{props.gender}</p>
  );
}



// ========================================

render(<OboutMe name="Alex" gender='girl' />, document.getElementById('root'));
